// アイテムリストにアイテム情報を追加する関数
function addItemList(itemName, category, rarity, cost, uniqueHero,
                     text, life, armor, shield, weaponPower,
                     abilityPower, attackSpeed, ctReducation, ammo, weapon_LifeSteal,
                     ability_LifeSteal, speed, reloadSpeed, meleeDamage, critical, others) {
    
    // アイテム情報を連想配列にする
    let itemData = {};
    itemData[itemNameKey] = itemName;
    itemData[categoryKey] = category;
    itemData[rarityKey] = rarity;
    itemData[costKey] = cost;
    itemData[uniqueHeroKey] = uniqueHero;
    itemData[textKey] = text;
    itemData[lifeKey] = life;
    itemData[armorKey] = armor;
    itemData[shieldKey] = shield;
    itemData[weaponPowerKey] = weaponPower;
    itemData[abilityPowerKey] = abilityPower;
    itemData[attackSpeedKey] = attackSpeed;
    itemData[ctReducationKey] = ctReducation;
    itemData[ammoKey] = ammo;
    itemData[weapon_LifeStealKey] = weapon_LifeSteal;
    itemData[ability_LifeStealKey] = ability_LifeSteal;
    itemData[speedKey] = speed;
    itemData[reloadSpeedKey] = reloadSpeed;
    itemData[meleeDamageKey] = meleeDamage;
    itemData[criticalKey] = critical;
    itemData[othersKey] = others;

    // アイテム情報をアイテムリストに追加
    itemList.push(itemData);
}